import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Employee = () => {
  const [users, setUser] = useState([]);
  const name = "";

  useEffect(() => {
    loadUsers();
  }, []);

  const loadUserData = async () => {
    const res = await axios
      .post(`http://localhost:8080/api/employee/checkemployeelogin`, employee)
      .then((res) => {
        if (res.data.status === 1) {
          console.log(res);
          console.log(res.data.name);
          name = res.data.name;
        } else {
          alert("Invalid Credential");
        }
      })
      .catch((err) => {
        alert("Invalid Credentials");
      });
  };

  const loadUsers = async () => {
    const result = await axios.get(
      "http://localhost:8080/api/detailsemployee/allemployee"
    );
    setUser(result.data);
    console.log(result.data);
  };

  const deleteEmployee = async (id) => {
    await axios.delete(`http://localhost:8080/api/detailsemployee/${id}`);
    alert(`${id} is deleted`);
    loadUsers();
  };

  return (
    <div>
      <div className="container">
        <div className="py-4">
          <h1>Home Page</h1>
          <h2>Welcome</h2>
          <Link className="btn btn-warning" to={"/addemployee"}>
            Add Employee
          </Link>
          <br />
          <br />
          <table className="table border shadow">
            <thead className="thead-dark">
              <tr>
                <th scope="col">#</th>
                <th scope="col">Name</th>
                <th scope="col">Email</th>
                <th scope="col">Address</th>
                <th scope="col">Role</th>
                <th scope="col">Department</th>
                <th scope="col">Action</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user, index) => (
                <tr>
                  <th scope="row">{index + 1}</th>
                  <td>{user.Name}</td>
                  <td>{user.Email}</td>
                  <td>{user.Address}</td>
                  <td>{user.Role}</td>
                  <td>{user.Department}</td>
                  <td>
                    <Link
                      className="btn btn-primary mr-2"
                      to={`/viewemployee/${user.id}`}
                    >
                      View
                    </Link>
                    <Link
                      className="btn btn-outline-primary mr-2"
                      to={`/editemployee/${user.id}`}
                    >
                      Edit
                    </Link>
                    <Link
                      className="btn btn-danger"
                      onClick={() => deleteEmployee(user.id)}
                    >
                      Delete
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Employee;
